import produce from "immer";

const reducer = (cases = {}, defaultState = {}) => (
  state = defaultState,
  action
) =>
  produce(state, draft => {
    if (action && action.type && cases[action.type] instanceof Function) {
      cases[action.type](draft, action);
    }
  });

export default reducer;
